import { useState } from "react";
import { json } from "@remix-run/node";
import { useLoaderData, useSubmit } from "@remix-run/react";
import {
  Page,
  Card,
  IndexTable,
  Thumbnail,
  Button,
  Modal,
  TextField,
  FormLayout,
  Layout,
  EmptyState,
  Text,
} from "@shopify/polaris";

