// Action function (giữ nguyên)
export const action = async ({ request }) => {
  const formData = await request.formData();
  const action = formData.get("action");

  if (action === "add") {
    return json({ success: true });
  } else if (action === "delete") {
    return json({ success: true });
  }

  return json({ error: "Invalid action" });
};

export default function ProductList() {
  const { products: initialProducts } = useLoaderData();
  const submit = useSubmit();
  const [products, setProducts] = useState(initialProducts);
  const [showAddModal, setShowAddModal] = useState(false);
  const [newProduct, setNewProduct] = useState({
    title: "",
    price: "",
    inventory: "",
    vendor: "",
  });
  const [selectedItems, setSelectedItems] = useState([]);

  const handleAddProduct = () => {
    const newId = Math.floor(Math.random() * 10000).toString();
    const productToAdd = {
      id: newId,
      ...newProduct,
      media: { url: "data:image/jpeg;base64," },
    };
    setProducts([...products, productToAdd]);
    setNewProduct({ title: "", price: "", inventory: "", vendor: "" });
    setShowAddModal(false);
    submit({ action: "add", ...newProduct, id: newId }, { method: "post" });
  };

  const handleDeleteSelected = () => {
    const updatedProducts = products.filter(
      (product) => !selectedItems.includes(product.id)
    );
    setProducts(updatedProducts);
    setSelectedItems([]);
    submit({ action: "delete", ids: selectedItems.join(",") }, { method: "post" });
  };

  const emptyStateContent = (
    <EmptyState
      heading="Quản lý sản phẩm của bạn"
      action={{ content: "Thêm sản phẩm", onAction: () => setShowAddModal(true) }}
      image="/api/placeholder/320/220"
    >
      <p>Thêm sản phẩm để bắt đầu bán hàng</p>
    </EmptyState>
  );

  const resourceName = {
    singular: "sản phẩm",
    plural: "sản phẩm",
  };

  const headings = [
    { title: "Hình ảnh" },
    { title: "Tên sản phẩm" },
    { title: "Giá" },
    { title: "Tồn kho" },
    { title: "Nhà cung cấp" },
  ];

  return (
    <Page
      title="Danh sách sản phẩm"
      primaryAction={{
        content: "Thêm sản phẩm",
        onAction: () => setShowAddModal(true),
      }}
    >
      <Layout>
        <Layout.Section>
          <Card>
            {products.length === 0 ? (
              emptyStateContent
            ) : (
              <IndexTable
                resourceName={resourceName}
                itemCount={products.length}
                selectedItemsCount={selectedItems.length}
                onSelectionChange={(selectionType, toggled, selection) => {
                  // Cập nhật selectedItems dựa trên selectionType và selection
                  if (selectionType === "page" || selectionType === "all") {
                    // Nếu chọn toàn bộ trang hoặc tất cả, lấy tất cả ID
                    setSelectedItems(products.map((product) => product.id));
                  } else {
                    // Nếu chọn đơn lẻ hoặc nhiều mục, sử dụng selection (mảng ID)
                    setSelectedItems(selection || []);
                  }
                }}
                headings={headings}
                promotedBulkActions={[
                  {
                    content: "Xóa sản phẩm",
                    onAction: handleDeleteSelected,
                    disabled: selectedItems.length === 0,
                  },
                ]}
              >
                {products.map((item, index) => {
                  const { id, title, price, inventory, vendor, media } = item;
                  const formattedPrice = new Intl.NumberFormat("vi-VN", {
                    style: "currency",
                    currency: "VND",
                  }).format(price);

                  return (
                    <IndexTable.Row
                      id={id}
                      key={id}
                      selected={Array.isArray(selectedItems) && selectedItems.includes(id)}
                      position={index}
                    >
                      <IndexTable.Cell>
                        <Thumbnail source={media.url} alt={title} size="small" />
                      </IndexTable.Cell>
                      <IndexTable.Cell>
                        <Text variant="bodyMd" fontWeight="bold" as="span">
                          {title}
                        </Text>
                      </IndexTable.Cell>
                      <IndexTable.Cell>{formattedPrice}</IndexTable.Cell>
                      <IndexTable.Cell>{inventory}</IndexTable.Cell>
                      <IndexTable.Cell>{vendor}</IndexTable.Cell>
                    </IndexTable.Row>
                  );
                })}
              </IndexTable>
            )}
          </Card>
        </Layout.Section>
      </Layout>

      {/* Modal thêm sản phẩm (giữ nguyên) */}
      <Modal
        open={showAddModal}
        onClose={() => setShowAddModal(false)}
        title="Thêm sản phẩm mới"
        primaryAction={{
          content: "Thêm",
          onAction: handleAddProduct,
          disabled: !newProduct.title || !newProduct.price,
        }}
        secondaryActions={[
          {
            content: "Hủy",
            onAction: () => setShowAddModal(false),
          },
        ]}
      >
        <Modal.Section>
          <FormLayout>
            <TextField
              label="Tên sản phẩm"
              value={newProduct.title}
              onChange={(value) => setNewProduct({ ...newProduct, title: value })}
              autoComplete="off"
              requiredIndicator
            />
            <TextField
              label="Giá (VND)"
              value={newProduct.price}
              onChange={(value) => setNewProduct({ ...newProduct, price: value })}
              type="number"
              autoComplete="off"
              requiredIndicator
            />
            <TextField
              label="Số lượng tồn kho"
              value={newProduct.inventory}
              onChange={(value) => setNewProduct({ ...newProduct, inventory: value })}
              type="number"
              autoComplete="off"
            />
            <TextField
              label="Nhà cung cấp"
              value={newProduct.vendor}
              onChange={(value) => setNewProduct({ ...newProduct, vendor: value })}
              autoComplete="off"
            />
          </FormLayout>
        </Modal.Section>
      </Modal>
    </Page>
  );
}